      const sortedPlugins = _.sortBy(
        this.loadedPlugins,
        (plugin) => plugin.constructor.name
      );

      this.consoleLog(sortedPlugins.map((plugin) => plugin.constructor.name).join(', '));
    } else {
      this.consoleLog('No plugins added yet');
    }
  }

