  }

  consoleLog(' ');
  consoleLog(chalk.yellow(` ${messageType} ${line}`));
  consoleLog(' ');


  let logLine = [];
  words.forEach(word => {
    logLine.push(word);
    const logLineString = logLine.join(' ');
    if (logLineString.length > 50) {
      consoleLog(chalk.yellow(`     ${logLineString}`));
      logLine = [];
    }
