  'aws-nodejs',
  'aws-python',
  'aws-groovy-gradle',
  'aws-java-maven',
  'aws-java-gradle',
  'aws-scala-sbt',
  'aws-csharp',
  'azure-nodejs',
  'openwhisk-nodejs',
  'plugin',
];

